				// DdN > 0, DdQxE2 < 0
				b.multiplyScalar( - 1 );
				intersect = ray.intersectTriangle( a, b, c, false );
				assert.strictEqual( intersect, null, "No intersection" );

				// DdN > 0, DdE1xQ < 0
				a.multiplyScalar( - 1 );
				intersect = ray.intersectTriangle( a, b, c, false );
				assert.strictEqual( intersect, null, "No intersection" );

