				a.multiplyScalar( - 1 );
				b.multiplyScalar( - 1 );
				ray.direction.multiplyScalar( - 1 );
				intersect = ray.intersectTriangle( a, b, c, false );
				assert.strictEqual( intersect, null, "No intersection when looking in the wrong direction" );

